"use strict";
import logo from './IMG_0004.PNG';
import './App.css';
import * as React from 'react';
import Box from '@mui/material/Box';
import BottomNavigation from '@mui/material/BottomNavigation';
import BottomNavigationAction from '@mui/material/BottomNavigationAction';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Paper from '@mui/material/Paper';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import Typography from '@mui/material/Typography';
import Card from '@mui/material/Card';
import useSound from "use-sound";
import bowl from './A_resonant_trimmed_2.mp3';
import dong from './A_dong_trimmed.wav'
import InfoIcon from '@mui/icons-material/Info';
import PeopleIcon from '@mui/icons-material/People';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import MusicNoteIcon from '@mui/icons-material/MusicNote';

function refreshMessages() {
  const getRandomInt = (max) => Math.floor(Math.random() * Math.floor(max));

  return Array.from(new Array(50)).map(
    () => messageExamples[getRandomInt(messageExamples.length)],
  );
}

function App (){

  const [playDong, { stop }] = useSound(dong);

  const [value, setValue] = React.useState(0);



  return (
    <div className="App"> 

      <header className="App-header">

        <button onMouseEnter={() => playDong()} >
          <img src={logo} className="App-logo" alt="logo"/>
        </button>

        <a className = "Centering" >
          Centering
        </a>

      <Typography variant="h6" > 
        A place for creation, reflection, and connection
      </Typography>
      </header> 

      <Box sx={{ width: 1 }}>
        <Paper elevation={3}>
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        <BottomNavigationAction label="Description" icon={<InfoIcon />} />
        <BottomNavigationAction label="Events" icon={<CalendarMonthIcon />} />
        <BottomNavigationAction label="About Us" icon={<PeopleIcon />} />
      </BottomNavigation>
        </Paper>

        <List>
        {messages.map(({ primary, secondary, person }, index) => (
          <ListItem button key={index + person}>

            <ListItemText primary={primary} secondary={secondary} />
          </ListItem>
        ))}
      </List>
    </Box>

      <Typography variant="h6" component="span" align="left"> 

      </Typography>


    </div>
  );

}

const messageExamples = [
  {
    primary: 'Brunch this week?',
    secondary: "I'll be in the neighbourhood this week. Let's grab a bite to eat",
    person: '/static/images/avatar/5.jpg',
  },
  {
    primary: 'Birthday Gift',
    secondary: `Do you have a suggestion for a good present for John on his work
      anniversary. I am really confused & would love your thoughts on it.`,
    person: '/static/images/avatar/1.jpg',
  },
  {
    primary: 'Recipe to try',
    secondary: 'I am try out this new BBQ recipe, I think this might be amazing',
    person: '/static/images/avatar/2.jpg',
  },
  {
    primary: 'Yes!',
    secondary: 'I have the tickets to the ReactConf for this year.',
    person: '/static/images/avatar/3.jpg',
  },
  {
    primary: "Doctor's Appointment",
    secondary: 'My appointment for the doctor was rescheduled for next Saturday.',
    person: '/static/images/avatar/4.jpg',
  },
  {
    primary: 'Discussion',
    secondary: `Menus that are generated by the bottom app bar (such as a bottom
      navigation drawer or overflow menu) open as bottom sheets at a higher elevation
      than the bar.`,
    person: '/static/images/avatar/5.jpg',
  },
  {
    primary: 'Summer BBQ',
    secondary: `Who wants to have a cookout this weekend? I just got some furniture
      for my backyard and would love to fire up the grill.`,
    person: '/static/images/avatar/1.jpg',
  },
];

export default App;
